    try {
        lookupList = delegator.findByAnd (org.ofbiz.shark.SharkConstants.WfEventAudit, UtilMisc.toMap (org.ofbiz.shark.SharkConstants.auditType, "processCreated", org.ofbiz.shark.SharkConstants.processId, processId));
