public double Type3a_Krawitz (int n) {
    int q = - 1;
    double sum = 0;
    q = 0;
    while (q < n) {
        sum += q;
        q ++;
    }
    if (n == 0) return sum;
    else return sum / n;

}

public double Type3b_Krawitz (int t) {
    int p = - 1, tot = 0;
    for (p = 0; p < t; p ++) tot += p;

    if (t == 0) return (double) tot;
    else return (double) tot / t;

}

