void sumProd2D (int n) {
    double sum = 0.0;
    double prod = 1.0;
    for (int i = 1;
    i <= n; i ++) {
        sum = sum + (i * i);
        prod = prod * (i * i);
        foo (sum, prod);
    }
}

void sumProd4D (int n) {
    double sum = 0.0;
    double prod = 1.0;
    int i = 0;
    while (i <= n) {
        sum = sum + i;
        prod = prod * i;
        foo (sum, prod);
        i ++;
    }
}
