        PyErr_SetString(AudioopError, "Strings should be even-sized");
        return 0;
}

if ( len1 != len2 ) {
        PyErr_SetString(AudioopError, "Samples should be same size");
        return 0;
}

if ( len1 & 1 ) {
        PyErr_SetString(AudioopError, "Strings should be even-sized");
        return 0;
}

if ( len1 < len2 ) {
        PyErr_SetString(AudioopError, "Input sample should be longer");
        return 0;
}

if ( len1 != len2 ) {
        PyErr_SetString(AudioopError, "Lengths should be the same");
        return 0;
}

if ( i < 0 || i >= len/size ) {
        PyErr_SetString(AudioopError, "Index out of range");
        return 0;
}

if ( len1 != len2 ) {
        PyErr_SetString(AudioopError, "Lengths should be the same");
        return 0;
}

if ( i < 0 || i >= len/size ) {
        PyErr_SetString(AudioopError, "Index out of range");
        return 0;
}

if ( len1 & 1 || len2 & 1 ) {
        PyErr_SetString(AudioopError, "Strings should be even-sized");
        return 0;
}

if ( len1 < len2 ) {
        PyErr_SetString(AudioopError, "First sample should be longer");
        return 0;
}

if ( len1 < len2 ) {
        PyErr_SetString(AudioopError, "Input sample should be longer");
        return 0;
}

if ( len1 & 1 ) {
        PyErr_SetString(AudioopError, "Strings should be even-sized");
        return 0;
}

if ( len1 != len2 ) {
        PyErr_SetString(AudioopError, "Samples should be same size");
        return 0;
}

if ( len1 & 1 || len2 & 1 ) {
        PyErr_SetString(AudioopError, "Strings should be even-sized");
        return 0;
}

		if (lenobj != NULL) {
//#if !defined(HAVE_LARGEFILE_SUPPORT)
//			l.l_len = PyInt_AsLong(lenobj);
//#else
			l.l_len = PyLong_Check(lenobj) ?
					PyLong_AsLongLong(lenobj) :
					PyInt_AsLong(lenobj);
//#endif
			if (PyErr_Occurred())
				return NULL;
		}

		if (startobj != NULL) {
//#if !defined(HAVE_LARGEFILE_SUPPORT)
//			l.l_start = PyInt_AsLong(startobj);
//#else
			l.l_start = PyLong_Check(startobj) ?
					PyLong_AsLongLong(startobj) :
					PyInt_AsLong(startobj);
//#endif
			if (PyErr_Occurred())
				return NULL;
		}

if ( diff >= step ) {
        delta = 4;
        diff -= step;
        vpdiff += step;
}

if ( diff >= step  ) {
        delta |= 2;
        diff -= step;
        vpdiff += step;
}

if ( state == Py_None ) {
        /* First time, it seems. Set defaults */
        valpred = 0;
        step = 7;
        index = 0;
} else if ( !PyArg_ParseTuple(state, "ii", &valpred, &index) )

if ( state == Py_None ) {
        /* First time, it seems. Set defaults */
        valpred = 0;
        step = 7;
        index = 0;
} else if ( !PyArg_ParseTuple(state, "ii", &valpred, &index) )

if ( state == Py_None ) {
        /* First time, it seems. Set defaults */
        valpred = 0;
        step = 7;
        index = 0;
} else if ( !PyArg_ParseTuple(state, "ii", &valpred, &index) )

if ( state == Py_None ) {
        /* First time, it seems. Set defaults */
        valpred = 0;
        step = 7;
        index = 0;
} else if ( !PyArg_ParseTuple(state, "ii", &valpred, &index) )

if ( diff >= step ) {
        delta = 4;
        diff -= step;
        vpdiff += step;
}

if ( diff >= step  ) {
        delta |= 2;
        diff -= step;
        vpdiff += step;
}

		if (lenobj != NULL) {
//#if !defined(HAVE_LARGEFILE_SUPPORT)
//			l.l_len = PyInt_AsLong(lenobj);
//#else
			l.l_len = PyLong_Check(lenobj) ?
					PyLong_AsLongLong(lenobj) :
					PyInt_AsLong(lenobj);
//#endif
			if (PyErr_Occurred())
				return NULL;
		}

		if (startobj != NULL) {
//#if !defined(HAVE_LARGEFILE_SUPPORT)
//			l.l_start = PyInt_AsLong(startobj);
//#else
			l.l_start = PyLong_Check(startobj) ?
					PyLong_AsLongLong(startobj) :
					PyInt_AsLong(startobj);
//#endif
			if (PyErr_Occurred())
				return NULL;
		}

if (len <= IOCTL_BUFSZ) {
	memcpy(buf, str, len);
	buf[len] = '\0';
	arg = buf;
}

else {
	memcpy(buf, str, len);
	buf[len] = '\0';
	arg = buf;
}

if (len <= IOCTL_BUFSZ) {
	memcpy(buf, str, len);
	buf[len] = '\0';
	arg = buf;
}

else {
	memcpy(buf, str, len);
	buf[len] = '\0';
	arg = buf;
}

if ( result < best_result ) {
        best_result = result;
        best_j = j;
}

if ( result > best_result ) {
        best_result = result;
        best_j = j;
}

if ( result > best_result ) {
        best_result = result;
        best_j = j;
}

if ( result < best_result ) {
        best_result = result;
        best_j = j;
}

if (prev_i == NULL || cur_i == NULL) {
        (void) PyErr_NoMemory();
        goto exit;
}

if (PyTuple_Size(samps) != nchannels) {
        PyErr_SetString(AudioopError,
                        "illegal state argument");
        goto exit;
}

                for (chan = 0; chan < nchannels; chan++) {
                        if (!PyArg_ParseTuple(PyTuple_GetItem(samps, chan),
                                              "ii:ratecv", &prev_i[chan], 
					                   &cur_i[chan]))
                                goto exit;
                }

if (str == NULL) {
        PyErr_SetString(PyExc_MemoryError,
                "not enough memory for output buffer");
        goto exit;
}

if (str == NULL) {
        PyErr_SetString(PyExc_MemoryError,
                "not enough memory for output buffer");
        goto exit;
}

                for (chan = 0; chan < nchannels; chan++) {
                        if (!PyArg_ParseTuple(PyTuple_GetItem(samps, chan),
                                              "ii:ratecv", &prev_i[chan], 
					                   &cur_i[chan]))
                                goto exit;
                }

if (PyTuple_Size(samps) != nchannels) {
        PyErr_SetString(AudioopError,
                        "illegal state argument");
        goto exit;
}

if (prev_i == NULL || cur_i == NULL) {
        (void) PyErr_NoMemory();
        goto exit;
}

